import React from "react";
import { connect } from "react-redux";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import PropTypes from "prop-types";
import { Login, Todos } from "./pages";

const routes = {
  login: {
    path: "/login",
    component: Login,
    private: false,
  },
  home: {
    path: "/",
    component: Todos,
    private: true,
  },
  noMatch: {
    path: "*",
    component: <>No Match</>,
    private: false,
  },
};

function PrivateRoute({ children, ...rest }) {
  const signIn = false;
  const { path, location } = rest;
  return (
    <Route
      path={path}
      location={location}
      render={() => {
        return signIn ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: "/login",
              state: { from: path },
            }}
          />
        );
      }}
    />
  );
}

PrivateRoute.defaultProps = {
  children: {},
};

PrivateRoute.propTypes = {
  children: PropTypes.symbol,
};

function RouteWithSubRoutes(route) {
  return !route.priavte ? (
    <Route
      path={route.path}
      render={() => <route.component component={route.component} />}
    />
  ) : (
    <PrivateRoute path={route.path}>
      <route.component component={route.component} />
    </PrivateRoute>
  );
}

function RouteConfig(props) {
  console.log(props.authState.signIn);
  return (
    <Router>
      <Switch>
        {Object.values(routes).map((route) => (
          <RouteWithSubRoutes
            key={route.path}
            path={route.path}
            component={route.component}
            priavte={route.private}
          />
        ))}
      </Switch>
    </Router>
  );
}

RouteConfig.propTypes = {
  authState: PropTypes.exact({
    signIn: PropTypes.bool,
  }),
};

const mapStateToProps = (state) => ({
  authState: state.authState,
});

export default connect(mapStateToProps)(RouteConfig);
