/* eslint-disable react/jsx-props-no-spreading */
import React from "react";
import { connect } from "react-redux";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import PropTypes from "prop-types";
import { Login, Todos } from "./pages";

export const routeConfig = {
  login: {
    path: "/login",
    component: Login,
    privateRoute: false,
  },
  test: {
    path: "/test",
    component: SentryComponent,
    routes: {
      testSentry: {
        path: "/test/sentry",
        component: () => <div>test Sentry</div>,
      },
    },
  },
  home: {
    path: "/",
    component: Todos,
    privateRoute: true,
  },
  noMatch: {
    path: "*",
    component: () => <div>No Match</div>,
    privateRoute: false,
  },
};
function RouteWithSubRoutes(route) {
  return (
    <Route
      path={route.path}
      render={() => {
        return !route.privateRoute ? (
          <>
            <route.component component={route.component} />
            {route.routes && (
              <Switch>
                {Object.values(route.routes).map((prop) => (
                  <RouteWithSubRoutes
                    key={prop.path}
                    path={prop.path}
                    component={prop.component}
                    privateRoute={prop.privateRoute}
                    routes={prop.routes}
                  />
                ))}
              </Switch>
            )}
          </>
        ) : (
          <Redirect
            to={{
              pathname: route.login.path,
              state: { from: route.path },
            }}
          />
        );
      }}
    />
  );
}
function RouteConfig(props) {
  const { routes, authState, ...rest } = props;
  return (
    <Router>
      <Switch>
        {Object.values(routes).map((route) => (
          <Route
            path={route.path}
            render={() => {
              return route.privateRoute && !authState.signIn ? (
                <>
                  <route.component routes={route.routes} {...rest} />
                </>
              ) : (
                <Redirect
                  to={{
                    pathname: route.login.path,
                    state: { from: route.path },
                  }}
                />
              );
            }}
          />
          // <RouteWithSubRoutes
          //   key={route.path}
          //   path={route.path}
          //   component={route.component}
          //   privateRoute={route.privateRoute && !authState.signIn}
          //   routes={route.routes}
          // />
        ))}
      </Switch>
    </Router>
  );
}
RouteConfig.defaultProps = {
  authState: {
    signIn: false,
  },
};
RouteConfig.propTypes = {
  authState: PropTypes.exact({
    signIn: PropTypes.bool,
  }),
  routes: PropTypes.any,
};
// RouteWithSubRoutes.defaultProps = {};
// RouteWithSubRoutes.propTypes = {};
const mapStateToProps = (state) => ({
  authState: state.authState,
});
export default connect(mapStateToProps)(RouteConfig);
