function RouteConfig(props) {
  const { authState, route } = props;

  return route ? (
    <Router>
      <Switch>
        {Object.values(routesConfig).map((item) => (
          <RouteConfig
            key={item.path}
            path={item.path}
            component={item.component}
            privateRoute={item.privateRoute && !authState.signIn}
            routes={item.routes}
          />
        ))}
      </Switch>
    </Router>
  ) : (
    <Route
      path={route.path}
      render={() => {
        return !route.privateRoute ? (
          <>
            <route.component component={route.component} />
            {route.routes && (
              <Switch>
                {Object.values(route.routes).map((prop) => (
                  <RouteConfig key={prop.path} route={prop} />
                ))}
              </Switch>
            )}
          </>
        ) : (
          <Redirect
            to={{
              pathname: routesConfig.login.path,
              state: { from: route.path },
            }}
          />
        );
      }}
    />
  );
}

RouteConfig.defaultProps = {
  authState: {
    signIn: false,
  },
  route: undefined,
};

RouteConfig.propTypes = {
  authState: PropTypes.exact({
    signIn: PropTypes.bool,
  }),
  route: PropTypes.objectOf(PropTypes.object),
};

// RouteWithSubRoutes.defaultProps = {};

// RouteWithSubRoutes.propTypes = {};

const mapStateToProps = (state) => ({
  authState: state.authState,
});

export default connect(mapStateToProps)(RouteConfig);
